import UIKit
import PKYStepper
import BraintreeDropIn
import Braintree

class CheckOutViewController: BaseViewController {

    @IBOutlet weak var steper: PKYStepper!
    
    var numberOfPhotos:Int = 1
    
    override func viewDidLoad() {
        super.viewDidLoad()

        self.addNavBackBtn(withSelector: #selector(goBack))
        self.setupSterpperView()
    }

    override func didReceiveMemoryWarning() {
        super.didReceiveMemoryWarning()
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        navigationItem.title = "Checkout"        
    }
    
    
    //MARK: - custom methods
    
    func fetchClientToken() {
        // TODO: Switch this URL to your own authenticated API
        let clientTokenURL = NSURL(string: "https://braintree-sample-merchant.herokuapp.com/client_token")!
        let clientTokenRequest = NSMutableURLRequest(url: clientTokenURL as URL)
        clientTokenRequest.setValue("text/plain", forHTTPHeaderField: "Accept")
        
        URLSession.shared.dataTask(with: clientTokenRequest as URLRequest) { (data, response, error) -> Void in
            // TODO: Handle errors
//            let clientToken = String(data: data!, encoding: String.Encoding.utf8)
            let clientToken = ""
            
            // As an example, you may wish to present Drop-in at this point.
            // Continue to the next section to learn more...
            }.resume()
    }
    
    func showDropIn(clientTokenOrTokenizationKey: String) {
        let request =  BTDropInRequest()
        let dropIn = BTDropInController(authorization: clientTokenOrTokenizationKey, request: request)
        { (controller, result, error) in
            if (error != nil) {
                print("ERROR")
            } else if (result?.isCancelled == true) {
                print("CANCELLED")
            } else if let result = result {
                // Use the BTDropInResult properties to update your UI
                // result.paymentOptionType
                // result.paymentMethod
                // result.paymentIcon
                // result.paymentDescription
            }
            controller.dismiss(animated: true, completion: nil)
        }
        self.present(dropIn!, animated: true, completion: nil)
    }

}

extension CheckOutViewController {
    func setupSterpperView() {
        steper.tintColor = UIColor.white
        steper.value = 1
        steper.stepInterval = 1
        steper.minimum = 1
        steper.maximum = 10
        steper.valueChangedCallback = { stepper, count in
            let cnt:Int = Int(count)
            self.numberOfPhotos = cnt
            self.steper.countLabel.text = "\(cnt)"
        }
        steper.setup()
    }
}
